#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\ASUS\Desktop\前端学习\learn-koa\node_modules\.pnpm\node@22.9.0\node_modules\node\bin\node_modules;C:\Users\ASUS\Desktop\前端学习\learn-koa\node_modules\.pnpm\node@22.9.0\node_modules\node\node_modules;C:\Users\ASUS\Desktop\前端学习\learn-koa\node_modules\.pnpm\node@22.9.0\node_modules;C:\Users\ASUS\Desktop\前端学习\learn-koa\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/ASUS/Desktop/前端学习/learn-koa/node_modules/.pnpm/node@22.9.0/node_modules/node/bin/node_modules:/mnt/c/Users/ASUS/Desktop/前端学习/learn-koa/node_modules/.pnpm/node@22.9.0/node_modules/node/node_modules:/mnt/c/Users/ASUS/Desktop/前端学习/learn-koa/node_modules/.pnpm/node@22.9.0/node_modules:/mnt/c/Users/ASUS/Desktop/前端学习/learn-koa/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

# Support pipeline input
if ($MyInvocation.ExpectingInput) {
  $input | & "$basedir/../../bin/node"   $args
} else {
  & "$basedir/../../bin/node"   $args
}
$env:NODE_PATH=$env_node_path
exit $LASTEXITCODE
